import { useEffect, useState } from "react";
import { FaBell } from "react-icons/fa";
import axios from "axios";

const Home = ({ user }) => {
    const [notifications, setNotifications] = useState([]);
    const [showNotifications, setShowNotifications] = useState(false);

    useEffect(() => {
        if (user) {
            fetchNotifications();
        }
    }, [user]);

    const fetchNotifications = async () => {
        try {
            const res = await axios.get(`http://localhost:5000/notifications?role=${user.role}`);
            setNotifications(res.data);
        } catch (error) {
            console.error("Lỗi tải thông báo:", error);
        }
    };

    const markNotificationsRead = async () => {
        try {
            await axios.put("http://localhost:5000/notifications/mark-read", { role: user.role });
            setNotifications([]);
        } catch (error) {
            console.error("Lỗi đánh dấu thông báo đã đọc:", error);
        }
    };

    return (
        <div className="relative">
            <span
                className="nav-item relative"
                onClick={() => {
                    setShowNotifications(!showNotifications);
                    markNotificationsRead();
                }}
                title="Thông báo"
            >
                <FaBell />
                {notifications.length > 0 && <span className="notification-badge">{notifications.length}</span>}
            </span>

            {showNotifications && (
                <div className="dropdown-menu">
                    <h3 className="dropdown-title">Thông báo</h3>
                    <ul className="dropdown-list">
                        {notifications.length === 0 ? (
                            <li className="empty-notif">Không có thông báo</li>
                        ) : (
                            notifications.map((notif) => (
                                <li key={notif.id} className="notif-item">
                                    <p>{notif.message}</p>
                                    <span className="notif-time">{new Date(notif.created_at).toLocaleString()}</span>
                                </li>
                            ))
                        )}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Home;
