            <ul>
                {schedule.map((classItem) => (
                    <li key={classItem.id}>
                        <strong>{classItem.name} ({classItem.subject})</strong> <br />
                        ‚è∞ L·ªãch h·ªçc: {classItem.schedule.split("; ").map((time, index) => (
                            <div key={index}>üìå {time}</div>
