            setLoading(false); // ƒê√°nh d·∫•u k·∫øt th√∫c t·∫£i d·ªØ li·ªáu
        } catch (error) {
            console.error(error);
            setFees([]); // ƒê·∫£m b·∫£o m·∫£ng h·ªçc ph√≠ tr·ªëng khi c√≥ l·ªói
            setLoading(false);
