import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import QRCode from "qrcode.react";
import "./Fees.css";

const Payments = ({ user }) => {
    const navigate = useNavigate();
    const [fees, setFees] = useState([]);
    const [showQR, setShowQR] = useState(false); // Tr·∫°ng th√°i hi·ªÉn th·ªã QR popup

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
                return;
            }
            fetchFees(storedUser.id);
        } else {
            fetchFees(user.id);
        }
    }, [user, navigate]);

    const fetchFees = async (studentId) => {
        try {
            const response = await fetch(`http://localhost:5000/student/fees/${studentId}`);
            if (!response.ok) throw new Error("L·ªói khi t·∫£i h·ªçc ph√≠");
            const data = await response.json();
            setFees(data);
        } catch (error) {
            console.error(error);
            setFees([]);
        }
    };

    // T·ªïng s·ªë ti·ªÅn c·∫ßn thanh to√°n
    const totalAmount = fees.reduce((sum, fee) => sum + (fee.remaining || 0), 0);

    // N·ªôi dung giao d·ªãch
    const transactionDescription = `HK2, 2024-2025, MSSV${user?.studentId}, So tien ${totalAmount.toLocaleString()}d`;

    // M√£ QR Code ch·ª©a th√¥ng tin giao d·ªãch
    const qrData = `STK:7410612345|TEN:Truong Dai hoc Can Tho|NH:BIDV|AMT:${totalAmount}|ND:${transactionDescription}`;

    return (
        <div className="fees-container">
            <h2>üí≥ Thanh to√°n h·ªçc ph√≠</h2>
            {fees.length === 0 ? (
                <p>Ch∆∞a c√≥ d·ªØ li·ªáu h·ªçc ph√≠.</p>
            ) : (
                <>
                    <table className="fees-table">
                        <thead>
                            <tr>
                                <th>üìö S·ªë m√¥n ƒëƒÉng k√Ω</th>
                                <th>üí∞ T·ªïng h·ªçc ph√≠</th>
                                <th>üìÖ Ng√†y ƒë√≥ng g·∫ßn nh·∫•t</th>
                                <th>üí≥ ƒê√£ thanh to√°n</th>
                                <th>üí∏ C√≤n l·∫°i</th>
                            </tr>
                        </thead>
                        <tbody>
                            {fees.map((fee) => (
                                <tr key={fee.id}>
                                    <td>{fee.subject_count}</td>
                                    <td>{fee.total_fee?.toLocaleString()} VNƒê</td>
                                    <td>{fee.latest_pay_at ? new Date(fee.latest_pay_at).toLocaleDateString() : "Ch∆∞a ƒë√≥ng"}</td>
                                    <td>{(fee.already_pay || 0).toLocaleString()} VNƒê</td>
                                    <td>{(fee.remaining || 0).toLocaleString()} VNƒê</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {fees.some(fee => fee.remaining > 0) && (
                        <div className="payment-section">
                            <h3>üîπ Khu v·ª±c thanh to√°n</h3>
                            <p>T·ªïng s·ªë ti·ªÅn c·∫ßn thanh to√°n: <strong>{totalAmount.toLocaleString()} VNƒê</strong></p>

                            {/* N√∫t m·ªü popup QR */}
                            <button className="pay-button qr-button" onClick={() => setShowQR(true)}>
                                N·ªôp h·ªçc ph√≠ qua QR
                            </button>
                        </div>
                    )}
                </>
            )}

            {/* Popup QR Code */}
            {showQR && (
                <div className="qr-popup">
                    <div className="qr-content">
                        <h3>N·ªôp h·ªçc ph√≠ qua ng√¢n h√†ng BIDV</h3>
                        <p><strong>Ng√¢n h√†ng:</strong> BIDV</p>
                        <p><strong>M√£ giao d·ªãch:</strong> CTU{user?.studentId}{Date.now()}</p>
                        <p><strong>M√¥ t·∫£:</strong> {transactionDescription}</p>
                        <p><strong>S·ªë ti·ªÅn:</strong> {totalAmount.toLocaleString()} VNƒê</p>
                        <p><strong>Qu√©t QR ƒë·ªÉ thanh to√°n:</strong></p>
                        <QRCode value={qrData} size={200} level="H" />

                        <button className="close-button" onClick={() => setShowQR(false)}>ƒê√≥ng</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Payments;
