import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import { FaHome, FaBell, FaSignOutAlt } from "react-icons/fa";
import Revenue from "./Revenue";
import axios from "axios";
import "./Home.css";

const socket = io("http://localhost:5000");

const Home = ({ user, token }) => {
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user || JSON.parse(localStorage.getItem("user")));
    const [notifications, setNotifications] = useState([]);
    const [schedule, setSchedule] = useState([]);

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
            return;
        }
        fetchSchedule(currentUser.id);
        socket.emit("join", { userId: currentUser.id });
        socket.on("newNotification", (notification) => {
            setNotifications((prev) => [notification, ...prev]);
        });
        return () => socket.off("newNotification");
    }, [currentUser, navigate]);

    const fetchSchedule = async (studentId) => {
        try {
            const response = await axios.get(`http://localhost:5000/student/schedule/${studentId}`);
            setSchedule(response.data);
        } catch (error) {
            console.error("Lỗi khi lấy lịch học:", error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("user");
        navigate("/login");
    };

    if (!currentUser) return <p>Đang tải...</p>;

    return (
        <div className="home-container">
            {/* Navbar */}
            <nav className="navbar">
                <h1>Trung Tâm Gia Sư</h1>
                <div className="navbar-buttons">
                    <span className="nav-item" onClick={() => navigate("/home")} title="Trang chủ">
                        <FaHome />
                    </span>
                    <span className="nav-item" onClick={() => navigate("/notifications")} title="Thông báo">
                        <FaBell />
                    </span>
                    <span className="nav-item logout" onClick={handleLogout} title="Đăng xuất">
                        <FaSignOutAlt />
                    </span>
                </div>
            </nav>

            <div className="main-content">
                {/* Sidebar */}
                <Sidebar role={currentUser.role} navigate={navigate} />

                {/* Main Dashboard Content */}
                <main className="dashboard">
                    {currentUser.role === "hv" && <StudentSchedule schedule={schedule} />}
                    {(currentUser.role === "cm" || currentUser.role === "ad") && <RevenueDashboard />}
                </main>
            </div>
        </div>
    );
};

const Sidebar = ({ role, navigate }) => {
    const sidebarItems = {
        hv: [
            { path: "/grades", label: "Điểm số" },
            { path: "/fees", label: "Học phí" },
            { path: "/makeup-classes", label: "Học bù" },
        ],
        gv: [
            { path: "/teaching-schedule", label: "Lịch giảng dạy" },
            { path: "/student-grades-input", label: "Chấm điểm" },
        ],
        cm: [
            { path: "/manage-groups", label: "Quản lý nhóm học" },
            { path: "/finance-reports", label: "Báo cáo tài chính" },
        ],
        ad: [
            { path: "/system-management", label: "Quản lý hệ thống" },
            { path: "/finance-reports", label: "Báo cáo tài chính" },
        ],
    };

    return (
        <aside className="sidebar">
            <ul>
                {sidebarItems[role]?.map((item, index) => (
                    <li key={index} className="sidebar-item" onClick={() => navigate(item.path)}>
                        {item.label}
                    </li>
                ))}
            </ul>
        </aside>
    );
};

const StudentSchedule = ({ schedule }) => {
    const daysOfWeek = ["Thứ 2", "Thứ 3", "Thứ 4", "Thứ 5", "Thứ 6", "Thứ 7", "Chủ Nhật"];
    return (
        <section className="dashboard-content">
            <h2>Lịch học của bạn</h2>
            <table className="schedule-table">
                <thead>
                    <tr>
                        {daysOfWeek.map((day, index) => (
                            <th key={index}>{day}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        {daysOfWeek.map((day, index) => (
                            <td key={index}>
                                {schedule.filter((item) => item.day === day).map((item, i) => (
                                    <div key={i} className={`schedule-item ${item.session}`}>{item.subject} ({item.time})</div>
                                ))}
                            </td>
                        ))}
                    </tr>
                </tbody>
            </table>
        </section>
    );
};

const RevenueDashboard = () => (
    <section className="dashboard-content">
        <h2>Thống kê doanh thu</h2>
        <div className="chart-container">
            <Revenue />
        </div>
    </section>
);

export default Home;
