import { useNavigate } from "react-router-dom";
import { QRCodeCanvas } from "qrcode.react";
import "./Fees.css";

const Payments = () => {
    const navigate = useNavigate();
    const [fees, setFees] = useState([]);
    const [isLoading, setIsLoading] = useState(true); // üî• State loading
    const [paymentMethod, setPaymentMethod] = useState("bank");
    const [showQR, setShowQR] = useState(false);
    const [user, setUser] = useState(null);

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login"); // N·∫øu kh√¥ng c√≥ ng∆∞·ªùi d√πng, ƒëi·ªÅu h∆∞·ªõng v·ªÅ login
        } else {
            setUser(storedUser); // C·∫≠p nh·∫≠t th√¥ng tin ng∆∞·ªùi d√πng t·ª´ localStorage
            fetchFees(storedUser.id); // G·ªçi API l·∫•y d·ªØ li·ªáu h·ªçc ph√≠
        }
    }, [navigate]);

    const fetchFees = async (studentId) => {
        setIsLoading(true);
        try {
            const response = await fetch(`http://localhost:5000/student/fees/${studentId}`);
            if (!response.ok) throw new Error("Failed to fetch fees");
            const data = await response.json();
            console.log("D·ªØ li·ªáu t·ª´ server:", data); // Ki·ªÉm tra d·ªØ li·ªáu tr·∫£ v·ªÅ t·ª´ server
            setFees(data);
        } catch (error) {
            console.error("L·ªói khi fetch d·ªØ li·ªáu h·ªçc ph√≠:", error);
            setFees([]);
        }
        setIsLoading(false);
    };
    const totalUnpaid = fees
        .filter(fee => fee.remaining > 0)  // Ch·ªâ l·∫•y nh√≥m ch∆∞a ƒë√≥ng ti·ªÅn
        .reduce((sum, fee) => sum + fee.remaining, 0);

    const handlePayment = async () => {
        const unpaidFees = fees.filter(fee => fee.remaining > 0);
        if (unpaidFees.length === 0) {
            alert("‚ùå Kh√¥ng c√≥ kho·∫£n n√†o c·∫ßn thanh to√°n.");
            return;
        }

        const paymentData = {
            studentId: user?.id,
            amount: unpaidFees.reduce((sum, fee) => sum + fee.remaining, 0) // Ch·ªâ thanh to√°n kho·∫£n ch∆∞a ƒë√≥ng
        };

        console.log("üí∞ D·ªØ li·ªáu thanh to√°n:", paymentData);

        const res = await fetch("http://localhost:5000/student/pay", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(paymentData)
        });

        const result = await res.json();
        console.log("üîÑ K·∫øt qu·∫£ thanh to√°n:", result);
        if (result.success) {
            fetchFees(user.id); // Reload d·ªØ li·ªáu
        }
    };





    const generateQRValue = () => {
        const totalAmount = fees.reduce((sum, fee) => sum + (fee.remaining || 0), 0);
        return `BIDV|CTUB${user.id}|HK2, 2024-2025, MSSV${user.id}, So tien ${totalAmount} VNƒê`;
    };

    // üî• Hi·ªÉn th·ªã loading khi d·ªØ li·ªáu ch∆∞a t·∫£i xong
    if (isLoading) {
        return <p>üîÑ ƒêang t·∫£i d·ªØ li·ªáu...</p>;
    }

    return (
        <div className="fees-container">
            <h2>üí≥ Thanh to√°n h·ªçc ph√≠</h2>

            {fees.length === 0 ? (
                <p>Ch∆∞a c√≥ d·ªØ li·ªáu h·ªçc ph√≠.</p>
            ) : (
                <>
                    <table className="fees-table">
                        <thead>
                            <tr>
