                                <th>üí∞ T·ªïng h·ªçc ph√≠</th>
                                <th>üìÖ Ng√†y ƒë√≥ng g·∫ßn nh·∫•t</th>
                                <th>üí≥ ƒê√£ thanh to√°n</th>
                                <th>üí∏ C√≤n l·∫°i</th>
                            </tr>
                        </thead>
                        <tbody>
                            {fees.map((fee) => (
                                <tr key={fee.id} style={{ backgroundColor: fee.remaining > 0 ? "#ffebee" : "#e8f5e9" }}>
                                    <td>{fee.group_count}</td>
