import React, { useState, useEffect } from "react";
import axios from "axios";

const TeacherGradeInput = () => {
    const [students, setStudents] = useState([]);
    const [scores, setScores] = useState({});

    useEffect(() => {
        axios.get("http://localhost:5000/student/user")
            .then(res => setStudents(res.data))
            .catch(err => console.error("Lỗi khi lấy danh sách học viên:", err));
    }, []);

    const handleScoreChange = (studentId, value) => {
        setScores(prev => ({ ...prev, [studentId]: value }));
    };

    const handleSubmit = (studentId) => {
        const score = scores[studentId];
        if (!score) return alert("Vui lòng nhập điểm!");

        axios.post("http://localhost:5000/teacher/grades", {
            student_id: studentId,
            score: parseFloat(score),
            exam_name: "Bài kiểm tra",
            exam_date: new Date().toISOString().split('T')[0]
        }).then(() => {
            alert("Nhập điểm thành công!");
            setScores(prev => ({ ...prev, [studentId]: "" }));
        }).catch(err => console.error("Lỗi khi nhập điểm:", err));
    };

    return (
        <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-lg">
            <h2 className="text-2xl font-semibold mb-4 text-center">Nhập Điểm Học Viên</h2>
            <table className="w-full border-collapse border border-gray-300">
                <thead>
                    <tr className="bg-gray-200 text-gray-700">
                        <th className="border p-2">ID</th>
                        <th className="border p-2">Họ Tên</th>
                        <th className="border p-2">Nhập Điểm</th>
                        <th className="border p-2">Xác Nhận</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map(student => (
                        <tr key={student.id} className="hover:bg-gray-100">
                            <td className="border p-2 text-center">{student.id}</td>
                            <td className="border p-2">{student.fullName}</td>
                            <td className="border p-2 text-center">
                                <input
                                    type="number"
                                    className="border rounded px-2 py-1 w-20 text-center"
                                    value={scores[student.id] || ""}
                                    onChange={(e) => handleScoreChange(student.id, e.target.value)}
                                    placeholder="0-100"
                                    min="0"
                                    max="100"
                                />
                            </td>
                            <td className="border p-2 text-center">
                                <button
                                    onClick={() => handleSubmit(student.id)}
                                    className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded"
                                >
                                    Xác nhận
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TeacherGradeInput;
