import { useEffect, useState } from "react";

const TeacherClassRegister = () => {
    const [classes, setClasses] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/api/available-classes") // Điều chỉnh URL nếu cần
            .then((res) => res.json())
            .then((data) => setClasses(data))
            .catch((error) => console.error("❌ Lỗi khi tải danh sách lớp:", error));
    }, []);

    return (
        <div>
            <h2>Danh sách lớp đang mở</h2>
            <table border="1" cellPadding="8" cellSpacing="0">
                <thead>
                    <tr>
                        <th>Tên lớp</th>
                        <th>Môn học</th>
                        <th>Trình độ</th>
                        <th>Khối</th>
                        <th>Sĩ số</th>
                        <th>Lịch học</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {classes.map((cls) => (
                        <tr key={cls.id}>
                            <td>{cls.name}</td>
                            <td>{cls.subject}</td>
                            <td>{cls.type}</td>
                            <td>{cls.grade}</td>
                            <td>
                                {cls.current_student}/{cls.max_student}
                            </td>
                            <td>{cls.schedule || "Chưa có lịch"}</td>
                            <td>
                                <button onClick={() => alert(`Đăng ký dạy lớp ${cls.name}`)}>
                                    Đăng ký dạy
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TeacherClassRegister;
