import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Register.css";
const Register = () => {
    const [formData, setFormData] = useState({
        username: "",
        email: "",
        password: "",
        confirmPassword: "",
    });

    const [error, setError] = useState("");
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");

        console.log("Dữ liệu form gửi đi:", formData);

        if (!formData.username || !formData.email || !formData.password) {
            setError("Vui lòng điền đầy đủ thông tin!");
            return;
        }

        if (formData.password !== formData.confirmPassword) {
            setError("Mật khẩu xác nhận không khớp!");
            return;
        }

        try {
            const res = await axios.post(
                "http://localhost:5000/register",
                {
                    username: formData.username,
                    email: formData.email,
                    password: formData.password,
                },
                {
                    headers: {
                        "Content-Type": "application/json",
                    },
                }
            );

            console.log("Phản hồi từ server:", res.data);

            alert(res.data.message);
            navigate("/login");
        } catch (err) {
            console.error("Lỗi từ server:", err.response?.data || err.message);
            setError(err.response?.data?.message || "Đã xảy ra lỗi!");
        }
    };



    return (
        <div className="register-container">
            <h2>Đăng ký tài khoản</h2>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleSubmit}>
                <input type="text" name="username" placeholder="Tên đăng nhập" onChange={handleChange} required />
                <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
                <input type="password" name="password" placeholder="Mật khẩu" onChange={handleChange} required />
                <input type="password" name="confirmPassword" placeholder="Xác nhận mật khẩu" onChange={handleChange} required />
                <button type="submit">Đăng ký</button>
            </form>
        </div>
    );
};

export default Register;
