import React, { useState, useEffect } from "react";
import axios from "axios";
import "./TeacherGradesInput.css";

const TeacherGradeInput = () => {
    const [students, setStudents] = useState([]);
    const [selectedStudent, setSelectedStudent] = useState("");
    const [examName, setExamName] = useState("");
    const [score, setScore] = useState("");
    const [examDate, setExamDate] = useState("");
    const [grades, setGrades] = useState([]);

    // Lấy danh sách học viên
    useEffect(() => {
        axios.get("http://localhost:5000/student")
            .then(res => setStudents(res.data))
            .catch(err => console.error("Lỗi khi lấy danh sách học viên:", err));
    }, []);

    // Lấy điểm của học viên khi chọn
    const fetchGrades = (studentId) => {
        axios.get(`http://localhost:5000/student/grades/${studentId}`)
            .then(res => setGrades(res.data))
            .catch(err => console.error("Lỗi khi lấy điểm số:", err));
    };

    // Khi chọn học viên
    const handleStudentChange = (e) => {
        setSelectedStudent(e.target.value);
        fetchGrades(e.target.value);
    };

    // Gửi điểm lên server
    const handleSubmit = (e) => {
        e.preventDefault();
        if (!selectedStudent || !examName || !score || !examDate) {
            alert("Vui lòng nhập đầy đủ thông tin");
            return;
        }

        axios.post("http://localhost:5000/student/grades", {
            student_id: selectedStudent,
            exam_name: examName,
            score: parseFloat(score),
            exam_date: examDate
        }).then(() => {
            alert("Nhập điểm thành công!");
            setExamName("");
            setScore("");
            setExamDate("");
            fetchGrades(selectedStudent);
        }).catch(err => console.error("Lỗi khi nhập điểm:", err));
    };

    return (
        <div>
            <h2>Nhập Điểm Cho Học Viên</h2>
            <label>Chọn Học Viên:</label>
            <select value={selectedStudent} onChange={handleStudentChange}>
                <option value="">-- Chọn --</option>
                {students.map(student => (
                    <option key={student.id} value={student.id}>{student.fullName}</option>
                ))}
            </select>

            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Tên bài kiểm tra" value={examName} onChange={(e) => setExamName(e.target.value)} />
                <input type="number" placeholder="Điểm" value={score} onChange={(e) => setScore(e.target.value)} />
                <input type="date" value={examDate} onChange={(e) => setExamDate(e.target.value)} />
                <button type="submit">Nhập điểm</button>
            </form>

            <h3>Danh sách điểm</h3>
            <ul>
                {grades.map((g, index) => (
                    <li key={index}>{g.exam_name}: {g.score} ({g.exam_date})</li>
                ))}
            </ul>
        </div>
    );
};

export default TeacherGradeInput;
