import { useState, useEffect } from 'react';
import './TeacherSalary.css';

function TeacherSalary() {
    const [teachers, setTeachers] = useState([]);
    const [salaries, setSalaries] = useState({});

    useEffect(() => {
        // Giả sử bạn lấy danh sách giáo viên từ API hoặc từ localStorage
        const mockTeachers = [
            { id: 1, name: 'Nguyễn Văn D' },
            { id: 2, name: 'Trần Thị E' },
            { id: 3, name: 'Lê Minh F' },
        ];
        setTeachers(mockTeachers);
    }, []);

    const handleSalaryChange = (teacherId, salary) => {
        setSalaries((prev) => ({
            ...prev,
            [teacherId]: salary,
        }));
    };

    const handleSubmit = () => {
        // Xử lý ghi nhận lương giáo viên, ví dụ gọi API
        console.log('Lương giáo viên:', salaries);
    };

    return (
        <div className="teacher-salary">
            <h2>Ghi Nhận Lương Giáo Viên</h2>
            <table className="salary-table">
                <thead>
                    <tr>
                        <th>Tên giáo viên</th>
                        <th>Lương</th>
                    </tr>
                </thead>
                <tbody>
                    {teachers.map((teacher) => (
                        <tr key={teacher.id}>
                            <td>{teacher.name}</td>
                            <td>
                                <input
                                    type="number"
                                    min="0"
                                    value={salaries[teacher.id] || ''}
                                    onChange={(e) => handleSalaryChange(teacher.id, e.target.value)}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={handleSubmit}>Lưu Lương</button>
        </div>
    );
}

export default TeacherSalary;
